params.query:
SELECT DISTINCT ?figure ?figureLabel ?figureDescription ?section ?sectionLabel ?sectionDescription WHERE {

    ?figure wdt:P31 wd:Q12405827 .
    ?figure wdt:P1441 ?section .
    wd:Q1845  (wdt:P527)* ?chapter .  
    wd:Q1845  (wdt:P527)* ?chapter .

  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} 
}
LIMIT 10
