params.query:
PREFIX wd: \<http://www.wikidata.org/entity/\> 
PREFIX wdt: \<http://www.wikidata.org/prop/direct/\>

SELECT DISTINCT ?politician ?cause ?politicianLabel ?causeLabel WHERE {
   ?politician wdt:P106 wd:Q82955 .            # find items that have "occupation (P106): politician (Q82955)"
   ?politician wdt:P509|wdt:P1196 ?cause .     # with a P509 (cause of death) or P1196 (manner of death) claim
   ?cause wdt:P279* wd:Q149086 .               # ... where the cause is a subclass of (P279*) homicide (Q149086)
   # ?politician wdt:P39 wd:Q11696 .           # Uncomment this line to include only U.S. Presidents 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en" }
} LIMIT 10
