params.query:
SELECT DISTINCT ?writer ?writerLabel ?place ?placeLabel ?movement
WHERE
{
  # get all German writers of a literary movement
  ?writer wdt:P135 wd:<Object ID>;
          wdt:P106 wd:Q36180;
          wdt:P6886 wd:Q188.

  wd:<Object ID> rdfs:label ?movement.
  filter(lang(?movement) = "en")

  # relevant places
  ?writer wdt:P19|wdt:P20|(wdt:P69/wdt:P131)|(wdt:P108/wdt:P131) ?place.

  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }

}
